parameters.jsCode:
const raw = $input.first().json.form_response

// Updated regex to match new format
const regex = /Full Name:(.*?),\s*Contact Number:(.*?),\s*Email Address:(.*?),\s*Address:(.*?),\s*What date and time work best for you\?\:(.*?),\s*What position are you interested in\?\:(.*?),\s*Would you like to be notified about promotional services\?\:(.*)/;

const match = raw.match(regex);

if (match) {
  const preferredDateTimeRaw = match[5].trim();

  // Extract date and time from Preferred DateTime
  const dateMatch = preferredDateTimeRaw.match(/^(.*\d{4}) (\d{2}:\d{2})/);

  if (!dateMatch) {
    return [{ json: { error: "Failed to extract date/time from Preferred DateTime.", success: false } }];
  }

  const datePart = dateMatch[1];
  const timePart = dateMatch[2];
  const combined = `${datePart} ${timePart}`;

  const startDateTime = DateTime.fromFormat(
    combined,
    "EEEE, LLL dd, yyyy HH:mm",
    { zone: "Asia/Manila" }
  ).toISO();

  const endDateTime = DateTime.fromFormat(
    combined,
    "EEEE, LLL dd, yyyy HH:mm",
    { zone: "Asia/Manila" }
  ).plus({ hours: 1 }).toISO();

  return [
    {
      json: {
        success: true,
        "Full Name": match[1].trim(),
        "Contact Number": match[2].trim(),
        "Email Address": match[3].trim(),
        "Address": match[4].trim(),
        "Preferred DateTime": match[5].trim(),
        "Interested Position": match[6].trim(),
        "Promo Consent": match[7].trim(),
        "Start Date Time": startDateTime,
        "End Date Time": endDateTime
      }
    }
  ];
} else {
  return [{ json: { error: "No match found. Input format may be invalid.", success: false } }];
}

